<?php\n// Script to check deduction types and see which are statutory vs non-statutory\nrequire_once __DIR__.'/vendor/autoload.php';\n\n// Create a Laravel application instance\n$app = require_once __DIR__.'/bootstrap/app.php';\n\n// Create a request to initialize the application properly\n$app->make('Illuminate\Contracts\Console\Kernel')->bootstrap();\n\nuse App\Models\DeductionType;\nuse App\Models\Deduction;\n\n// Check all deduction types to see which are statutory\n$deductionTypes = DeductionType::all();\necho \"Deduction Types:\\n\";\nforeach ($deductionTypes as $type) {\n    echo \"  ID: {$type->id}, Name: {$type->name}, Is Statutory: \" . ($type->is_statutory ? 'Yes' : 'No') . \", Rate/Amount: {$type->rate_or_amount}\\n\";\n}\n\n// Check specific deductions for our employees\n$deductions211 = Deduction::where('employee_id', 211)->with('deductionType')->get();\necho \"\\nEmployee 211 Deductions:\\n\";\nforeach ($deductions211 as $ded) {\n    $type = $ded->deductionType;\n    echo \"  Deduction: {$ded->deduction_type}, Amount: {$ded->amount}, Date: {$ded->start_date}, Type ID: {$ded->deduction_type_id}\";\n    if ($type) {\n        echo \", Is Statutory: \" . ($type->is_statutory ? 'Yes' : 'No');\n    }\n    echo \"\\n\";\n}\n\n$deductions214 = Deduction::where('employee_id', 214)->with('deductionType')->get();\necho \"\\nEmployee 214 Deductions:\\n\";\nforeach ($deductions214 as $ded) {\n    $type = $ded->deductionType;\n    echo \"  Deduction: {$ded->deduction_type}, Amount: {$ded->amount}, Date: {$ded->start_date}, Type ID: {$ded->deduction_type_id}\";\n    if ($type) {\n        echo \", Is Statutory: \" . ($type->is_statutory ? 'Yes' : 'No');\n    }\n    echo \"\\n\";\n}\n?>